from nx_restraunt_orders as ror left join nx_community_goods as cg on cg.nx_community_goods_id = ror.nx_RO_com_goods_id left join nx_restraunt_user as ru on ru.nx_restraunt_user_id = ror.nx_RO_order_user_id left join nx_restraunt as rs on rs.nx_restraunt_id = ror.nx_RO_restraunt_father_id left join nx_community_user as cu on cu.nx_community_user_id = rs.nx_restraunt_driver_id
